package org.cf.forgot.jdk.oom;

/**
 * Description:
 * cd cf-forgot-jdk/target/classes
 * java -Xss5120k org.cf.forgot.jdk.oom.StackOFE
 *
 * @date 2020/10/10
 * @since
 */
public class StackOFE {
    private static long counter = 0;

    public static void stackOverFlowErrorMethod(String str) {
        try {
            counter++;
//            System.out.println("counter: " + counter);
            stackOverFlowErrorMethod(str);
        } catch (StackOverflowError error) {
            System.out.println("counter: " + counter);
//            error.printStackTrace();
            System.exit(0);
        }
    }

    public static void main(String[] args) {
        stackOverFlowErrorMethod(
                "");
    }

}